import React, { useState } from 'react';

const CreateProject = () => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    projectId: '',
    useExistingProject: false,
  });

  const handleInputChange = (event) => {
    const { name, value, type, checked } = event.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Here you would handle form submission, e.g., send formData to an API
    console.log('Form submitted:', formData);
  };

  return (
    <div>
      <h1>Create New Project</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Name:
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
            />
          </label>
        </div>

        <div>
          <label>
            Description:
            <textarea
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              required
            />
          </label>
        </div>

        <div>
          <label>
            Project ID:
            <textarea
              name="projectid"
              value={formData.projectid}
              onChange={handleInputChange}
              required
            />
          </label>
        </div>

        <div>
          <label>
            <input
              type="checkbox"
              name="useExistingProject"
              checked={formData.useExistingProject}
              onChange={handleInputChange}
            />
            Use Existing Project
          </label>
        </div>

        {formData.useExistingProject ? (
          <div>
            <label>
              Project ID:
              <input
                type="text"
                name="projectId"
                value={formData.projectId}
                onChange={handleInputChange}
              />
            </label>
          </div>
        ) : (
          <div>
            <p>New Project will be created.</p>
          </div>
        )}

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default CreateProject;
