// src/pages/CreateProject.js

import React, { useState } from 'react';

const CreateProject = () => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    projectId: '',
    useExistingProject: false,
  });

  const handleInputChange = (event) => {
    const { name, value, type, checked } = event.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('/create_project', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const result = await response.json();
      console.log('Project created:', result);
      // Optionally, handle the response (e.g., show a success message)
    } catch (error) {
      console.error('Error creating project:', error);
      // Optionally, handle the error (e.g., show an error message)
    }
  };

  return (
    <div>
      <h1>Create New Project</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Name:
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
            />
          </label>
        </div>

        <div>
          <label>
            Description:
            <textarea
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              required
            />
          </label>
        </div>

        <div>
          <label>
            <input
              type="checkbox"
              name="useExistingProject"
              checked={formData.useExistingProject}
              onChange={handleInputChange}
            />
            Use Existing Project
          </label>
        </div>

        {formData.useExistingProject ? (
          <div>
            <label>
              Project ID:
              <input
                type="text"
                name="projectId"
                value={formData.projectId}
                onChange={handleInputChange}
              />
            </label>
          </div>
        ) : (
          <div>
            <p>New Project will be created.</p>
          </div>
        )}

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default CreateProject;
